mapper.update:
	UPDATE information
	SET
		name = #{name}
     ,opentime = #{opentime}
     ,closetime = #{closetime}
     ,telNumber = #{telNumber}
     ,eadress = #{eadress}
     ,parking = #{parking}
     ,reservation = #{reservation}
     ,freecal = #{freecal}
     ,letter 	= #{letter}
  WHERE 1=1
  	AND a.seq = #{seq}
