mapper.select:
SELECT a.seq ,a.name ,a.opentime ,a.closetime ,a.telNumber ,a.eadress ,a.parkingNy ,a.ptNy ,a.groupNy ,a.letter ,a.regDate ,a.modDate ,a.specifics ,a.adress ,a.delny ,a.orders ,a.UseNy ,a.ceoName FROM information a WHERE 1=1 AND delny = #{shDelNy} AND UseNy = #{shUseNy}
AND seq=#{shValue} AND name=#{shValue} AND ceoName=#{shValue}
AND a.regdate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDate BETWEEN #{shDateStart} AND #{shDateEnd}
ORDER BY a.seq desc

mapper.update:
	UPDATE information
	SET
		name = #{name}
     ,opentime = #{opentime}
     ,closetime = #{closetime}
     ,telNumber = #{telNumber}
     ,eadress = #{eadress}
     ,parkingNy = #{parkingNy}
     ,ptNy = #{ptNy}
     ,letter 	= #{letter}
     ,groupNy = #{groupNy}
     ,letter = #{letter}
     ,specifics = #{specifics}
     ,adress = #{adress}
     ,delny = #{delny}
     ,orders = #{orders}
     ,UseNy = #{UseNy}
     ,ceoName= #{ceoName}
  WHERE 1=1
  	AND a.seq = #{seq}

mapper.delete:
DELETE FROM information
WHERE 1=1
	AND seq=#{seq}
